mapper.select:
SELECT si.id, si.gene_refgene, si.aachange_refgene, CONCAT(s.chrom, '-', s.pos) AS chromPos,
si.exonicfunc_refgene, si.xref_refgene, sf.sick AS sample_name, si.popfreqmax, si.a1000g_all,
si.a1000g_eas, si.exac_all, si.exac_eas, si.esp6500siv2_all, sf.mutation_ad, sf.mutation_mode, sf.mutation_rate
FROM tbl_snp s
JOIN tbl_sick sc ON s.sick_id=sc.id
JOIN tbl_snp_info si ON s.id = si.snp_id
JOIN tbl_snp_format sf ON s.id = sf.snp_id
WHERE 1=1
AND si.popfreqmax  >  '0.'

	AND si.popfreqmax  <=  #{popfreqmax}

AND si.exac_all  >  '0.'

	AND si.exac_all  <=  #{exacAll}

AND si.a1000g_all  >  '0.'

	AND si.a1000g_all  <=  #{a1000gAll}

AND si.exac_eas  >  '0.'

	AND si.exac_eas  <=  #{exacEas}


	AND sc.sick_code = #{sickCode}

AND si.aachange_refgene IS NOT NULL AND si.aachange_refgene NOT IN  ('UNKNOWN', 'unknown', '.', '')
ORDER BY si.id

	LIMIT #{offset}, #{limit}

SELECT si.id, si.gene_refgene, si.aachange_refgene, CONCAT(s.chrom, '-', s.pos) AS chromPos, si.exonicfunc_refgene, si.xref_refgene, sf.sick AS sample_name, si.popfreqmax, si.a1000g_all, si.a1000g_eas, si.exac_all, si.exac_eas, si.esp6500siv2_all, sf.mutation_ad, sf.mutation_mode, sf.mutation_rate FROM tbl_snp s JOIN tbl_snp_info si ON s.id = si.snp_id JOIN tbl_snp_format sf ON s.id = sf.snp_id WHERE 1=1 AND si.id IN
#{id}
ORDER BY si.id

SELECT COUNT(1)
FROM tbl_snp s
JOIN tbl_sick sc ON s.sick_id=sc.id
JOIN tbl_snp_info si ON s.id = si.snp_id
JOIN tbl_snp_format sf ON s.id = sf.snp_id
WHERE 1=1
AND si.popfreqmax  >  '0.'

	AND si.popfreqmax  <=  #{popfreqmax}

AND si.exac_all  >  '0.'

	AND si.exac_all  <=  #{exacAll}

AND si.a1000g_all  >  '0.'

	AND si.a1000g_all  <=  #{a1000gAll}

AND si.exac_eas  >  '0.'

	AND si.exac_eas  <=  #{exacEas}


	AND sc.sick_code = #{sickCode}

AND si.aachange_refgene IS NOT NULL AND si.aachange_refgene NOT IN  ('UNKNOWN', 'unknown', '.', '')
