mapper.select:
SELECT sa.sick_id, s.sick_name, CONCAT(s.sick_code, '-', p.id) as sick_code, sa.id as sample_id,
sa.upload_time, sa.trigger_time, sa.handler_time, sa.store_time, sa.finish_time,
sa.handler_status, sa.store_status, p.product_name
FROM tbl_sick s
JOIN tbl_sample sa ON s.id=sa.sick_id
JOIN tbl_sick_product sp ON s.id=sp.sick_id
JOIN tbl_product p ON sp.product_id=p.id


		and sa.sick_id = #{sickId}


		and s.sick_code = #{sickCode}


		and sa.id = #{sampleId}


order by sa.finish_time desc, sa.sick_id desc

	limit #{offset}, #{limit}

SELECT count(1)
FROM tbl_sick s
JOIN tbl_sample sa ON s.id=sa.sick_id
JOIN tbl_sick_product sp ON s.id=sp.sick_id
JOIN tbl_product p ON sp.product_id=p.id


		and sa.sick_id = #{sickId}


		and s.sick_code = #{sickCode}


		and sa.id = #{sampleId}
